if (ad_utils.inject_serverside_ad) {
ad_utils.inject_serverside_ad('top_ad', ' document.defaultAd = {h:0,w:0}; document.ad = document.defaultAd;</script>

 (function(window, document) {
 let SLOT_NAME = 'top_ad';
 let videoTargetMap = new Map(); 
 let validPlayIndicatorEvents = ['loadstart', 'play'];

 function makeId() {
 return Math.random().toString(36).substr(2, 16);
 }

 function handleVideoEvent(event) {
 let videoTarget = event.target;
 let eventType = event.type;
 let timestamp = Date.now();

 if (videoTarget.nodeName !== 'VIDEO' || videoTarget.loop) {
 return;
 }

 if (!videoTargetMap.has(videoTarget)) {
 let id = makeId();
 videoTargetMap.set(videoTarget, {
 id: id
 });
 videoTarget.id = id;
 }

 let videoTargetEventMap = videoTargetMap.get(videoTarget);
 videoTargetEventMap[eventType] = timestamp;

 if (eventType === 'ended') {
 let firedEvents = Object.keys(videoTargetEventMap);
 let hasMatchingPlayEvent = firedEvents.some(function(evt) {
 return validPlayIndicatorEvents.includes(evt);
 });
